mapper.select:
SELECT LAWYER_NUM , LAWYER_ID , LAWYER_PWD , LAWYER_NAME , LAWYER_BIRTHYY , LAWYER_GENDER , LAWYER_PHONE , LAWYER_EMAIL , LAWYER_LICENSE_NUM , LAWYER_INTRO_MSG FROM LAWYER_TB L LEFT JOIN COMPANY_TB C ON L.COMPANY_NUM = C.COMPANY_NUM WHERE 1=1
AND LAWYER_NUM = #{lawyerNum}
AND LAWYER_ID = #{lawyerId}

